mapper.select:
select

from video_trace_camera_config
where
ref_work_grid_key = #{refWorkGridKey,jdbcType=VARCHAR}

  and yn = #{yn,jdbcType=INTEGER}


  and status = #{status,jdbcType=TINYINT}


  and ref_work_station_key = #{refWorkStationKey,jdbcType=VARCHAR}


  and machine_code = #{machineCode,jdbcType=VARCHAR}


  and chute_code = #{chuteCode,jdbcType=VARCHAR}


  and supply_dws_code = #{cameraCode,jdbcType=VARCHAR}


  and master_camera = #{masterCamera,jdbcType=TINYINT}


  and camera_id = #{cameraId,jdbcType=INTEGER}


    and camera_id in

      #{cameraId,jdbcType=INTEGER}



  and create_time  <=   #{operateTime}
  and ((update_time  >=   #{operateTime} and yn =0)
  or yn=1)

limit 100

select
from video_trace_camera_config where yn = 1 and ref_work_grid_key = #{refWorkGridKey,jdbcType=VARCHAR} and (machine_code is null or machine_code = '') and (ref_work_station_key is null or ref_work_station_key = '')
and camera_id = #{cameraId,jdbcType=INTEGER}

mapper.update:
update video_trace_camera_config set
update_erp = #{updateErp,jdbcType=VARCHAR},
update_time = now(), yn=0 where id = #{id,jdbcType=INTEGER}
